from typing import List


class Solution:
    def chalkReplacer(self, chalk: List[int], k: int) -> int:
        size = len(chalk)
        for i in range(1, size):
            chalk[i] += chalk[i - 1]
        total = chalk[size - 1]
        rest = k % total
        if rest == 0:
            return 0
        for i in range(0, size):
            if rest < chalk[i]:
                return i

